import React, { useState } from 'react';
import {
  TextField,
  RadioGroup,
  FormControlLabel,
  Radio,
  Button,
  Typography,
  Container,
  Grid,
  Box,
  Stepper,
  Step,
  StepLabel,
  Paper,
  Card,
  CardMedia,
  CardContent
} from '@mui/material';

const vehicleOptions = {
  '4': {
    SUV: {
      id: 1,
      name: 'Fortuner',
      image: 'https://upload.wikimedia.org/wikipedia/commons/thumb/6/66/2015_Toyota_Fortuner_%28New_Zealand%29.jpg/1200px-2015_Toyota_Fortuner_%28New_Zealand%29.jpg'
    },
    Sedan: {
      id: 2,
      name: 'Honda City',
      image: 'data:image/webp;base64,'
  },

    Hatchback: {
      id: 3,
      name: 'Safari',
      image: 'data:image/jpeg;base64,'
  }

  },
  '2': {
    'Sports Bike': {
      id: 4,
      name: 'KTM Duke',
      image: 'https://cdn.bikedekho.com/processedimages/ktm/2023-200-duke/source/2023-200-duke66fea6b410ee9.jpg'
    },
    'Cruiser Bike': {
      id: 5,
      name: 'Royal Enfield',
      image: 'data:image/jpeg;base64,'
    },


    Scooter: {
      id: 6,
      name: 'Honda Activa',
      image: 'https://cdn.bikedekho.com/processedimages/honda/activa-6g/source/activa-6g65e80a524ce81.jpg'
    }
  }
};

const MultiStepForm = () => {
  const [step, setStep] = useState(0);
  const [submitted, setSubmitted] = useState(false);
  const [form, setForm] = useState({
    firstName: '',
    lastName: '',
    wheels: '',
    type: '',
    vehicleId: '',
    dates: { start: '', end: '' }
  });

  const steps = ['Personal Info', 'Wheel Type', 'Vehicle Type', 'Vehicle', 'Dates'];

  const handleNext = () => {
    if (
      (step === 0 && (!form.firstName || !form.lastName)) ||
      (step === 1 && !form.wheels) ||
      (step === 2 && !form.type) ||
      (step === 3 && !form.vehicleId) ||
      (step === 4 && (!form.dates.start || !form.dates.end))
    ) {
      alert('Please fill in all required fields.');
      return;
    }
    setStep(prev => prev + 1);
  };

  const handleBack = () => setStep(prev => prev - 1);

  const handleSubmit = () => {
    // Replace alert with a thank you screen
    setSubmitted(true);
  };

  const renderVehicleCard = (vehicle) => (
    <Card sx={{ display: 'flex', mb: 2 }}>
      <CardMedia
        component="img"
        sx={{ width: 120 }}
        image={vehicle.image}
        alt={vehicle.name}
      />
      <CardContent>
        <Typography variant="h6">{vehicle.name}</Typography>
      </CardContent>
    </Card>
  );

  const currentVehicleOptions = vehicleOptions[form.wheels] || {};

  if (submitted) {
    return (
      <Box
        sx={{
          minHeight: '100vh',
          display: 'flex',
          alignItems: 'center',
          justifyContent: 'center',
          flexDirection: 'column',
          textAlign: 'center',
          px: 3,
          backgroundColor: '#f5f5f5'
        }}
      >
        <img
          src="data:image/jpeg;base64,"
          alt="Thank you"
          style={{ width: '200px', marginBottom: '20px' }}
        />
        <Typography variant="h4" gutterBottom color="primary">
          Thank You!
        </Typography>
        <Typography variant="body1">
          Your form has been submitted successfully. We’ll get back to you soon.
        </Typography>
      </Box>
    );
  }

  return (
    <Box
      sx={{
        minHeight: '100vh',
        backgroundImage: `url('https://cdn.pixabay.com/photo/2024/07/13/07/40/cars-8891625_640.jpg')`,
        backgroundSize: 'cover',
        backgroundPosition: 'center',
        position: 'relative',
        py: 6,
        px: 2,
        '&::before': {
          content: '""',
          position: 'absolute',
          top: 0, left: 0, right: 0, bottom: 0,
          backgroundColor: 'rgba(0, 0, 0, 0.6)',
          backdropFilter: 'blur(2px)',
          zIndex: 1
        }
      }}
    >
      <Container maxWidth="sm" sx={{ position: 'relative', zIndex: 2 }}>
        <Paper elevation={10} sx={{
          borderRadius: 3,
          p: 4,
          background: 'linear-gradient(135deg, #ffffffcc, #e3f2fdcc)',
          backdropFilter: 'blur(10px)'
        }}>
          <Typography variant="h4" align="center" fontWeight="bold" gutterBottom color="primary">
            Book Your Ride
          </Typography>

          <Stepper activeStep={step} alternativeLabel sx={{ mb: 4 }}>
            {steps.map(label => (
              <Step key={label}>
                <StepLabel>
                  <Typography variant="body2" fontWeight="bold">{label}</Typography>
                </StepLabel>
              </Step>
            ))}
          </Stepper>

          {step === 0 && (
            <Grid container spacing={2}>
              <Grid item xs={12}>
                <TextField
                  fullWidth label="First Name" variant="outlined"
                  value={form.firstName}
                  onChange={e => setForm({ ...form, firstName: e.target.value })}
                />
              </Grid>
              <Grid item xs={12}>
                <TextField
                  fullWidth label="Last Name" variant="outlined"
                  value={form.lastName}
                  onChange={e => setForm({ ...form, lastName: e.target.value })}
                />
              </Grid>
            </Grid>
          )}

          {step === 1 && (
            <RadioGroup
              value={form.wheels}
              onChange={e => setForm({ ...form, wheels: e.target.value, type: '', vehicleId: '' })}
            >
              <FormControlLabel value="2" control={<Radio />} label="2 Wheels" />
              <FormControlLabel value="4" control={<Radio />} label="4 Wheels" />
            </RadioGroup>
          )}

          {step === 2 && (
            <RadioGroup
              value={form.type}
              onChange={e => {
                const selectedType = e.target.value;
                setForm({
                  ...form,
                  type: selectedType,
                  vehicleId: currentVehicleOptions[selectedType]?.name || ''
                });
              }}
            >
              {Object.keys(currentVehicleOptions).map(type => (
                <FormControlLabel
                  key={type}
                  value={type}
                  control={<Radio />}
                  label={type}
                />
              ))}
            </RadioGroup>
          )}

          {step === 3 && form.vehicleId && (
            <>
              {renderVehicleCard(currentVehicleOptions[form.type])}
              <Typography>Selected Vehicle: {form.vehicleId}</Typography>
            </>
          )}

          {step === 4 && (
            <Grid container spacing={2}>
              <Grid item xs={6}>
                <TextField
                  fullWidth
                  type="date"
                  label="Start Date"
                  InputLabelProps={{ shrink: true }}
                  value={form.dates.start}
                  onChange={e => setForm({ ...form, dates: { ...form.dates, start: e.target.value } })}
                />
              </Grid>
              <Grid item xs={6}>
                <TextField
                  fullWidth
                  type="date"
                  label="End Date"
                  InputLabelProps={{ shrink: true }}
                  value={form.dates.end}
                  onChange={e => setForm({ ...form, dates: { ...form.dates, end: e.target.value } })}
                />
              </Grid>
            </Grid>
          )}

          <Box sx={{ mt: 4, display: 'flex', justifyContent: 'space-between' }}>
            {step > 0 && (
              <Button variant="outlined" onClick={handleBack}>
                Back
              </Button>
            )}
            {step < steps.length - 1 ? (
              <Button variant="contained" onClick={handleNext}>
                Next
              </Button>
            ) : (
              <Button variant="contained" color="success" onClick={handleSubmit}>
                Submit
              </Button>
            )}
          </Box>
        </Paper>
      </Container>
    </Box>
  );
};

export default MultiStepForm;
